import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import bgImage from "assets/images/TaeApril22.jpg";
import axios from "axios";
import Button from "@mui/material/Button";
import { TextField, IconButton, InputAdornment } from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import ToglePWField from "./components/toglePassword";

import "./index.css";

export default function SignIn() {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");

  const [su_id, setSignupId] = useState("");

  const [su_email, setSignupEm] = useState("");
  const [emailError, setEmailError] = useState(false);

  const [su_phone, setSignupPh] = useState("");
  const movePage = useNavigate();
  const [showSignUp, setShowSignUp] = useState(false);
  const [hideSignUp, setHideSignUp] = useState(false);

  const onClickLogin = () => {
    console.log("click login");
    console.log("nickname : ", id);
    console.log("PW : ", password);
    axios
      .post("http://localhost:8080/authentication/sign-in", {
        nickname: id,
        pw: password,
      })
      .then((res) => {
        console.log(res);
        if (res.data.nickname === undefined) {
          // id 일치하지 않는 경우 userId = undefined, msg = '입력하신 id 가 일치하지 않습니다.'
          alert("입력하신 id 가 일치하지 않습니다.");
        } else if (res.data.nickname === null) {
          // id는 있지만, pw 는 다른 경우 userId = null , msg = undefined
          alert("입력하신 비밀번호 가 일치하지 않습니다.");
        } else if (res.data.nickname === id) {
          sessionStorage.setItem("user_id", id); // sessionStorage에 id를 user_id라는 key 값으로 저장
          movePage("/dashboard/main-page");
        }
      })
      .catch((error) => {
        console.error("API 호출 중 오류가 발생했습니다.", error);
        alert("로그인 중 오류가 발생했습니다. 다시 시도해주세요.");
      });
  };

  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  const handleEmailChange = (e) => {
    const email = e.target.value;
    setSignupEm(email);

    // 이메일 유효성 검사를 위한 정규표현식
    const emailRegex = /^[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}$/;

    if (!emailRegex.test(email)) {
      setEmailError(true);
    } else {
      setEmailError(false);
    }
  };

  return (
    <div className="bg-image" style={{ backgroundImage: `url(${bgImage})` }}>
      <div>
        <form className="signin-form-style">
          <h1 className="banner-center">Sign In</h1>

          <TextField
            id="id-input"
            label="ID"
            value={id}
            onChange={(e) => setId(e.target.value)}
            variant="outlined"
            margin="normal"
            fullWidth
            sx={{ zIndex: 0 }}
          />

          <TextField
            id="password-input"
            label="Password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            variant="outlined"
            margin="normal"
            fullWidth
            sx={{ zIndex: 0 }}
          />

          <Button
            type="button"
            variant="contained"
            onClick={onClickLogin}
            sx={{ marginTop: 2 }}
          >
            로그인
          </Button>

          <div
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "flex-end",
              height: "100%",
            }}
          >
            <p style={{ margin: "10px", textAlign: "center" }}>
              아직 회원이 아니신가요? &nbsp;
              <a
                className="blue-emphasis"
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
                onClick={() => setShowSignUp(true)}
              >
                회원가입
              </a>
              {showSignUp && (
                <div
                  className={`sign-up ${
                    hideSignUp
                      ? "slide-out-opacity"
                      : showSignUp
                      ? "slide-in-opacity"
                      : ""
                  }`}
                  onAnimationEnd={() => {
                    if (hideSignUp) {
                      setShowSignUp(false);
                      setHideSignUp(false);
                    }
                  }}
                >
                  <div
                    style={{
                      display: "flex",
                      justifyContent: "flex-end",
                      marginBottom: "10px",
                    }}
                  >
                    <span
                      className="close-btn"
                      onClick={() => {
                        setHideSignUp(true);
                      }}
                    >
                      &times;
                    </span>
                  </div>
                  <div style={{ marginTop: "100px" }}>
                    <h1>관리자 계정을 만들어주세요!</h1>
                    <div className="signup-form-style">
                      <TextField
                        id="setID"
                        label="ID"
                        value={su_id}
                        onChange={(e) => setSignupId(e.target.value)}
                        variant="standard"
                        margin="normal"
                        fullWidth
                        sx={{ marginBottom: "20px" }}
                      />
                      <ToglePWField />
                      <TextField
                        id="setEmail"
                        label="E-mail"
                        value={su_email}
                        onChange={handleEmailChange}
                        variant="standard"
                        margin="normal"
                        fullWidth
                        error={emailError}
                        helperText={
                          emailError ? "유효한 이메일 주소를 입력하세요." : ""
                        }
                        sx={{ marginBottom: "20px" }}
                      />

                      <TextField
                        id="setPhone"
                        label="Phone"
                        value={su_phone}
                        onChange={(e) => setSignupPh(e.target.value)}
                        variant="standard"
                        margin="normal"
                        fullWidth
                        sx={{ marginBottom: "20px" }}
                      />

                      <Button
                        type="button"
                        variant="contained"
                        //onClick={onClickLogin}
                        sx={{ marginTop: 4, width: "100%" }}
                      >
                        회원가입
                      </Button>
                      <hr />
                      <div style={{ marginTop: "20px" }}>
                        Already have an account? &nbsp;
                        <a
                          className="blue-emphasis"
                          onMouseEnter={handleMouseEnter}
                          onMouseLeave={handleMouseLeave}
                          onClick={() => movePage("/authentication/")} //있으면 로그인으로 돌아가도록
                        >
                          로그인
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </p>
          </div>
        </form>
      </div>
    </div>
  );
}
